import React, {Component} from 'react';

import ReactEcharts from 'echarts-for-react';
import echarts from 'echarts/lib/echarts';


class Chart extends Component {
    constructor(props){
        super(props);

        this.state = {

        };    
    };

    //echarts_instance = echarts.init(document.getElementById('chart'))
    setData = () => {
        let chart = echarts.init(document.getElementById('chart'));

        this.option.series[0].data = this.props.data;

        chart.setOption(this.option);
        console.log(this.option.series[0].data)
        this.forceUpdate();
    }



    option = {
        title: {
            text: this.props.title,
            textStyle: {
                color: '#FFF'
            }
        },
        tooltip: {
            trigger: 'axis',
            // formatter: function (params) {
            //     params = params[0];
            //     var date = new Date(params.name);
            //     return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
            // },
            axisPointer: {
                animation: false
            }
        },
        xAxis: {
            type: 'time',
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        yAxis: {
            type: 'value',
            boundaryGap: [0, '100%'],
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        series: [{
            name: 'CHART',
            type: 'line',
            showSymbol: false,
            hoverAnimation: false,
            data: [1,2,3]
        }]
    };




    render() {

        return (
            <div id="chart">
                <ReactEcharts

                    option={this.option}
                    style={{height: 360}}
                    opts={{renderer: 'svg'}}
                    onChartReady={this.setData}


                /> 
        </div>
        );
    }

};

export default Chart;
