import React from 'react';

import ReactEcharts from 'echarts-for-react';

const Chart = (props) => {
    const chartOptions = {
        title: {
            text: props.title,
        },
        tooltip: {
            trigger: 'axis',
            formatter: function (params) {
                params = params[0];
                var date = new Date(params.name);
                return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
            },
            axisPointer: {
                animation: false
            },
        },
        xAxis: {
            type: 'time',
            splitLine: {
                show: false
            },
            nameTextStyle: {
                color: "#FFFFFF",
                borderColor: "#FFFFFF",
                backgroundColor: "#FFFFFF",
                shadowColor: "#FFFFFF"
            },
            axisLine: {
                color: '#FFF',
                shadowColor: '#FFFFFF'
            }

        },
        yAxis: {
            type: 'value',
            boundaryGap: [0, '100%'],
            splitLine: {
                show: false
            },
        },
        series: [{
            name: '模拟数据',
            type: 'line',
            showSymbol: false,
            hoverAnimation: false,
            data: props.data
        }],


    };


    return (
        <div >
            <ReactEcharts
                option={chartOptions}
                style={{height: 360}}
                opts={{renderer: 'svg'}}
            />
       </div>
    );

};

export default Chart;
