import React, {Component} from 'react';

import ReactEcharts from 'echarts-for-react';
import echarts from 'echarts/lib/echarts';
import io from 'socket.io-client'


const server = 'wss://le-18262636.bitzonte.com';

class Chart extends Component {
    constructor(props){
        super(props);
        this.state = {

        };   

      };


    data = []

    componentDidMount() {
        const socket = io(server, {  
            path: '/stocks'
          });
        socket.on('UPDATE', (data) => {
            this.data.push(data.value);
            console.log(this.data)
    })}

    option = {
        title: {
            text: this.props.title,
            textStyle: {
                color: '#FFF'
            }
        },
        tooltip: {
            trigger: 'axis',
            // formatter: function (params) {
            //     params = params[0];
            //     var date = new Date(params.name);
            //     return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
            // },
            axisPointer: {
                animation: false
            }
        },
        xAxis: {
            type: 'time',
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        yAxis: {
            type: 'value',
            boundaryGap: [0, '100%'],
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        series: [{
            name: 'CHART',
            type: 'line',
            showSymbol: false,
            hoverAnimation: false,
            data: console.log(this.data)
        }]
    };





    render() {

        return (
            <div id="chart">
                <ReactEcharts
                    option={this.option}
                    style={{height: 360}}
                    opts={{renderer: 'svg'}}


                /> 
            </div>
        );
    }

};

export default Chart;
