import React, {Component} from 'react';

import ReactEcharts from 'echarts-for-react';
import echarts from 'echarts/lib/echarts';


class Chart extends Component {
    constructor(props){
        super(props);
        this.title = React.createRef()
        this.state = {

        };    
    };

    //echarts_instance = echarts.init(document.getElementById('chart'))
    onChartReadyCallback = () => {
        let chart = echarts.init(document.getElementById('chart'));

        this.option.series[0].data = this.props.data;

        chart.setOption(this.option);
        //console.log(document.getElementById('chart'))
        console.log(document.getElementsByClassName("echarts-for-react"))
        //console.log(this.title);
    }





    render() {
        let chart = echarts.init(document.getElementsByClassName("echarts-for-react"));

        const option = {
            title: {
                text: this.props.title,
                textStyle: {
                    color: '#FFF'
                }
            },
            tooltip: {
                trigger: 'axis',
                // formatter: function (params) {
                //     params = params[0];
                //     var date = new Date(params.name);
                //     return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
                // },
                axisPointer: {
                    animation: false
                }
            },
            xAxis: {
                type: 'time',
                splitLine: {
                    show: false
                },
                axisLine: {
                    lineStyle: {
                        color: '#FFF'
                    }
                }
            },
            yAxis: {
                type: 'value',
                boundaryGap: [0, '100%'],
                splitLine: {
                    show: false
                },
                axisLine: {
                    lineStyle: {
                        color: '#FFF'
                    }
                }
            },
            series: [{
                name: 'CHART',
                type: 'line',
                showSymbol: false,
                hoverAnimation: false,
                data: this.props.data
            }]
        };

        setInterval(function () {

            this.option.series[0].data = this.props.data;

            chart.setOption(option);
        }, 1000);

        return (
            <div id="chart">
                <ReactEcharts

                    option={option}
                    style={{height: 360}}
                    opts={{renderer: 'svg'}}
                    onChartReady={setInterval()}
                    notMerge={true}
                    lazyUpdate={true}


                /> 
        </div>
        );
    }

};

export default Chart;
