import React from 'react';

import ReactEcharts from 'echarts-for-react';

const Chart = (props) => {
    const chartOptions = {
        // title: {
        //     text: props.title,
        // },
        // tooltip: {
        //     trigger: 'axis',
        //     axisPointer: {         
        //         type: 'shadow'        
        //     }
        // },
        // grid: {
        //     left: '15px',
        //     right: '30px',
        //     top: '35px', //regulo el alto
        //     bottom: '30px', //con estos dos
        //     containLabel: true,
        // },
        // xAxis: [{
        //     type: 'category',
        //     data: props.time
        // }],
        // yAxis: [{
        //     type: 'value'
        // }],
        // series: [{
        //     name: 'CHART',
        //     data: props.data,
        //     type: 'line',
        //     smooth: true
        // }]
        title: {
            text: props.title,
        },
        tooltip: {
            trigger: 'axis',
            formatter: function (params) {
                params = params[0];
                var date = new Date(params.name);
                return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
            },
            axisPointer: {
                animation: false
            },
        },
        xAxis: {
            type: 'time',
            splitLine: {
                show: false
            },

        },
        yAxis: {
            type: 'value',
            boundaryGap: [0, '100%'],
            splitLine: {
                show: false
            },
        },
        series: [{
            name: 'Chart',
            type: 'line',
            showSymbol: false,
            hoverAnimation: false,
            data: props.data
        }],


    };


    return (
        <div>
            <ReactEcharts
                option={chartOptions}
                style={{height: 360}}
                opts={{renderer: 'svg'}}
            />
       </div>
    );

};

export default Chart;
