import React from 'react';

import ReactEcharts from 'echarts-for-react';
import echarts from 'echarts/lib/echarts';
import ZingChart from 'zingchart-react';

const Chart = (props) => {
    //var chart = echarts.init(document.getElementById(props.id))
    // console.log(props)
    var option = {
        title: {
            text: props.title,
            textStyle: {
                color: '#FFF'
            }
        },
        tooltip: {
            trigger: 'axis',
            // formatter: function (params) {
            //     params = params[0];
            //     var date = new Date(params.name);
            //     return date.getDate() + '/' + (date.getMonth() + 1) + '/' + date.getFullYear() + ' : ' + params.value[1];
            // },
            axisPointer: {
                animation: false
            }
        },
        xAxis: {
            type: 'time',
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        yAxis: {
            type: 'value',
            boundaryGap: [0, '100%'],
            splitLine: {
                show: false
            },
            axisLine: {
                lineStyle: {
                    color: '#FFF'
                }
            }
        },
        series: [{
            name: 'CHART',
            type: 'line',
            showSymbol: false,
            hoverAnimation: false,
            data: (function() {
                return props.data
            })
        }]
    };




    return (
        <div>
             <ReactEcharts
                option={option}
                style={{height: 360}}
                opts={{renderer: 'svg'}}


            /> 
       </div>
    );

};

export default Chart;
